    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="past-shapes-page">
            <h1>My Past Designs</h1>
            <div className="designs-grid">
                {pastShapes.map((shape, index) => (
                    <div key={index} className="design-card">
                        <p>Name: {shape.name}</p>
                        <p>Timestamp: {shape.timestamp?.toDate().toLocaleString()}</p>

                        {/* Display colors with face names */}
                        <div className="color-display">
                            {Object.entries(shape.designData).map(([face, color]) => (
                                <div key={face} className="color-row"> {/* New div for each row */}
                                    <span className="face-name">{face}:</span> {/* Face name */}
                                    <div className="color-square" style={{ backgroundColor: color }}></div>
                                    <span className="color-value">{color}</span> {/* Color value */}
                                </div>
                            ))}
                        </div>

                        {/* ... other design details if needed */}
                    </div>
                ))}
            </div>
            <Link to="/dashboard">Back to Dashboard</Link>
        </div>
    );
};

export default PastShapes;
