import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles/DashboardPage.css'; // Import your CSS file

const DashboardPage = () => {
    const [showProfileDropdown, setShowProfileDropdown] = useState(false);
    const [showShapesDropdown, setShowShapesDropdown] = useState(false);

    const availableShapes = [
        { id: 1, name: "Cube", thumbnail: "/cube-thumb.png" },
        { id: 2, name: "Sphere", thumbnail: "/sphere-thumb.png" },
        { id: 3, name: "Pyramid", thumbnail: "/pyramid-thumb.png" }
    ];

    const pastShapes = [
        { id: 1, name: "My Cube Design", date: "2024-01-15", thumbnail: "/design1.png" },
        { id: 2, name: "Cool Sphere", date: "2024-01-14", thumbnail: "/design2.png" },
        { id: 3, name: "Pyramid Project", date: "2024-01-13", thumbnail: "/design3.png" }
    ];

    return (
        <div className="dashboard-page">
            <nav className="navbar">
                <img src="/potterypickslogo.png" alt="Pottery Picks Logo" className="navbar-logo" />
                <h1 className="navbar-title">Pottery Picks</h1>
                <div className="auth-buttons">
                    <Link to="/">
                        <button className="auth-button logout">Log Out</button>
                    </Link>
                </div>
            </nav>

            <div className="dashboard-layout">
                <div className="dashboard-sidebar">
                    <div className="sidebar-item">
                        <button
                            className="dropdown-button"
                            onClick={() => setShowProfileDropdown(!showProfileDropdown)}
                        >
                            Edit Profile {showProfileDropdown ? <>&uarr;</> : <>&darr;</>}
                        </button>
                        {showProfileDropdown && (
                            <div className="dropdown-content">
                                <div className="profile-edit">
                                    <label htmlFor="name">Name:</label> {/* Added labels */}
                                    <input type="text" id="name" placeholder="Name" className="profile-input" />
                                    <label htmlFor="email">Email:</label>
                                    <input type="email" id="email" placeholder="Email" className="profile-input" />
                                    <label htmlFor="password">New Password:</label>
                                    <input type="password" id="password" placeholder="New Password" className="profile-input" />
                                    <button className="save-button">Save Changes</button>
                                </div>
                            </div>
                        )}
                    </div>

                    <div className="sidebar-item">
                        <button
                            className="dropdown-button"
                            onClick={() => setShowShapesDropdown(!showShapesDropdown)}
                        >
                            Past Shapes {showShapesDropdown ? <>&uarr;</> : <>&darr;</>}
                        </button>
                        {showShapesDropdown && (
                            <div className="dropdown-content">
                                {pastShapes.map(shape => (
                                    <div key={shape.id} className="past-shape-item">
                                        <img src={shape.thumbnail} alt={shape.name} className="shape-thumbnail" />
                                        <span>{shape.name}</span>
                                        <div className="shape-actions">
                                            <button className="action-button">View</button>
                                            <button className="action-button">Edit</button>
                                            <button className="action-button delete">Delete</button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>

                <div className="dashboard-main">
                    <h2 className="dashboard-greeting">Hello, User.</h2>

                    <div className="available-shapes">
                        <h3 className="section-title">Available Shapes</h3>
                        <div className="shapes-grid">
                            {availableShapes.map(shape => (
                                <div key={shape.id} className="shape-card">
                                    <img src={shape.thumbnail} alt={shape.name} className="shape-thumbnail" />
                                    <h4 className="shape-name">{shape.name}</h4>
                                </div>
                            ))}
                        </div>
                    </div>

                    <Link to="/app" className="create-design-button">
                        Start Designing
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default DashboardPage;
