import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles/DashboardPage.css'; // Import your CSS file

const DashboardPage = () => {
    const [showProfileDropdown, setShowProfileDropdown] = useState(false);
    const [showShapesDropdown, setShowShapesDropdown] = useState(false);
    const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);

    const availableShapes = [
        { id: 1, name: "Cube", thumbnail: "/cube-thumb.png" },
        { id: 2, name: "Sphere", thumbnail: "/sphere-thumb.png" },
        { id: 3, name: "Pyramid", thumbnail: "/pyramid-thumb.png" }
    ];

    const pastShapes = [
        { id: 1, name: "My Cube Design", date: "2024-01-15", thumbnail: "/design1.png" },
        { id: 2, name: "Cool Sphere", date: "2024-01-14", thumbnail: "/design2.png" },
        { id: 3, name: "Pyramid Project", date: "2024-01-13", thumbnail: "/design3.png" }
    ];

    return (
        <div className="dashboard-page">
            <nav className="navbar">
                <img src="/potterypickslogo.png" alt="Pottery Picks Logo" className="navbar-logo" />
                <h1 className="navbar-title">Pottery Picks</h1>
                <div className="auth-buttons">
                    <Link to="/">
                        <button className="auth-button logout">Log Out</button>
                    </Link>
                </div>
            </nav>
            <div className="dashboard-layout">
                <div className={`dashboard-sidebar ${isSidebarCollapsed ? 'collapsed' : ''}`}>
                    <button className="collapse-button" onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)}>
                        {isSidebarCollapsed ? <>&rarr;</> : <>&larr;</>}
                    </button>

                    <div className="sidebar-item">
                        <Link to="/editprofile">
                            <button className="dropdown-button">Edit Profile</button>
                        </Link>
                    </div>

                    <div className="sidebar-item">
                        <Link to="/pastshapes">
                            <button className="dropdown-button">Past Shapes</button>
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DashboardPage;
