import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { auth, db } from './firebase';
import './styles/DashboardPage.css';

const DashboardPage = () => {
    const [user, setUser] = useState(null);
    const [username, setUsername] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showProfileDropdown, setShowProfileDropdown] = useState(false);
    const [showShapesDropdown, setShowShapesDropdown] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const navigate = useNavigate();

    const availableShapes = [
        { id: 1, name: "Cube", thumbnail: "/cube-thumb.png" },
        { id: 2, name: "Sphere", thumbnail: "/sphere-thumb.png" },
        { id: 3, name: "Pyramid", thumbnail: "/pyramid-thumb.png" }
    ];

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged((user) => {
            if (user) {
                setUser(user);
                fetchUserData(user.uid);
            } else {
                navigate('/login');
            }
            setLoading(false);
        });

        return () => unsubscribe();
    }, [navigate]);

    const fetchUserData = async (uid) => {
        try {
            const userDoc = await db.collection('users').doc(uid).get();
            if (userDoc.exists) {
                setUsername(userDoc.data().username);
            }
        } catch (err) {
            setError('Failed to load user data');
        }
    };

    const handleLogout = async () => {
        try {
            await auth.signOut();
            navigate('/login');
        } catch (err) {
            setError('Failed to log out');
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Loading dashboard...</p>
            </div>
        );
    }

    return (
        <div className="dashboard-page">
            <nav className="navbar">
                <Link to="/" className="navbar-logo-link">
                    <img src="/potterypickslogo.png" alt="Pottery Picks Logo" className="navbar-logo" />
                    <h1 className="navbar-title">Pottery Picks</h1>
                </Link>
                <div className="auth-buttons">
                    <button onClick={handleLogout} className="auth-button logout">
                        Log Out
                    </button>
                </div>
            </nav>

            <div className="dashboard-layout">
                <button 
                    className="toggle-btn"
                    onClick={() => setIsOpen(!isOpen)}
                    aria-label="Toggle Sidebar"
                >
                    {isOpen ? '×' : '☰'}
                </button>

                <aside className={`dashboard-sidebar ${isOpen ? 'open' : ''}`}>
                    <div className="sidebar-container">
                        <div className="sidebar-item">
                            <button 
                                className="dropdown-button"
                                onClick={() => setShowProfileDropdown(!showProfileDropdown)}
                            >
                                Profile Settings
                                <span className="dropdown-icon">{showProfileDropdown ? '▼' : '▶'}</span>
                            </button>
                            {showProfileDropdown && (
                                <div className="dropdown-content">
                                    <div className="profile-edit">
                                        <input
                                            type="text"
                                            className="profile-input"
                                            value={username}
                                            onChange={(e) => setUsername(e.target.value)}
                                            placeholder="Username"
                                        />
                                        <button className="save-button">Save Changes</button>
                                    </div>
                                </div>
                            )}
                        </div>

                        <div className="sidebar-item">
                            <button 
                                className="dropdown-button"
                                onClick={() => setShowShapesDropdown(!showShapesDropdown)}
                            >
                                My Shapes
                                <span className="dropdown-icon">{showShapesDropdown ? '▼' : '▶'}</span>
                            </button>
                            {showShapesDropdown && (
                                <div className="dropdown-content shapes-list">
                                    <Link to="/past-shapes" className="view-all-shapes">
                                        View All Shapes
                                    </Link>
                                </div>
                            )}
                        </div>
                    </div>
                </aside>

                <main className="dashboard-main">
                    <h2 className="section-title">Available Shapes</h2>
                    <div className="shapes-grid">
                        {availableShapes.map((shape) => (
                            <div key={shape.id} className="shape-card">
                                <img 
                                    src={shape.thumbnail} 
                                    alt={shape.name} 
                                    className="shape-thumbnail"
                                />
                                <h3 className="shape-name">{shape.name}</h3>
                            </div>
                        ))}
                    </div>

                    <Link to="/app" className="create-design-button">
                        Create New Design
                    </Link>
                </main>
            </div>
        </div>
    );
};

export default DashboardPage;
